import React, { useContext } from "react";
import { createContext, useState } from "react";
import User from "./user";


export const userContext = createContext({
    user: null,
    loadUser: () => {},
    logIn: () => {},
    logOut: () => {} 
});
                  // id, displayName, email, isLoggedIn
const USER = new User(0, "Guest",      "",      false);

export function UserContextProvider({ children }) {
    const [user, setUser] = useState(USER);

    function loadUser() {
        setUser(localStorage.user[user])
    }

    function logIn(id, displayName, email, password, isLoggedIn) {
        setUser(new User(id, displayName, email, password, isLoggedIn));
        localStorage.setItem("user", user);

    }
    function logOut() {
        localStorage.removeItem("user");
        setUser(USER);
    }

    return (
        <userContext.Provider value={{ user, loadUser, logIn, logOut }}>
            { children }
        </userContext.Provider>
    );
}

export function useUserContext() {
    const {user, loadUser, logIn, logOut} = useContext(userContext);

    return {user, loadUser, logIn, logOut};
}
